import React, { useEffect, useState, Component, useCallback } from "react";
import {
  SafeAreaView,
  Text,
  TextInput,
  View,
  Button,
  Alert,
  FlatList,
  TouchableOpacity,
  Image,
  Input,
  ScrollView,
  StatusBar,
