import { CheckBox } from "@rneui/themed";
import SearchableDropdown from "react-native-searchable-dropdown";
import SelectDropdown from "react-native-select-dropdown";
import { render } from "react-dom";
import { useForm } from 'react-hook-form';

export const TestScreen = ({ navigation }) => {
  const { t, i18n } = useTranslation();


  const { register, handleSubmit, setValue } = useForm();
  const onSubmit = useCallback(formData => {
    console.log(formData);
    // const requestOption = {
    //   headers: {
    //     Authorization:
    //     "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJlbWFpbCI6InAubWFyYXNjYTg5QGdtYWlsLmNvbSIsImlkIjo4LCJpYXQiOjE2NzA0OTcwNTEsImV4cCI6MTY3MzE3NTQ1MX0.TvaEDRJwkGQYdWXeTbutep0_GdG1qPBDhHOgOmnkEFg",
    //   },
    //   method: "POST",
    //   body: formData
    //     .fetch(
    //       "https://api.tu-store.soluzionitop.cloud/api/customers",
    //       requestOption
    //     )
    //     .then((response) => console.log(response))
    //     //.then((data) => console.log(data)),
    // };

    //var dataToSend = {formData.email, formData.password};
    //making data to send on server
    var formBody = [];
    formBody = formData;
    //POST request
    fetch('https://api.tu-store.soluzionitop.cloud/api/customers', {
      method: 'POST', //Request Type
      body: formBody, //post body
      headers: {
        //Header Defination
        'Content-Type': 
          'application/x-www-form-urlencoded;charset=UTF-8',
          Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJlbWFpbCI6InAubWFyYXNjYTg5QGdtYWlsLmNvbSIsImlkIjo4LCJpYXQiOjE2NzA0OTcwNTEsImV4cCI6MTY3MzE3NTQ1MX0.TvaEDRJwkGQYdWXeTbutep0_GdG1qPBDhHOgOmnkEFg",
      },
    })
      .then((response) => response.json())
      //If response is in json then in success
      .then((responseJson) => {
        alert(JSON.stringify(responseJson));
        console.log(responseJson);
      })
      //If response is not in json then in error
      .catch((error) => {
        alert(JSON.stringify(error));
        console.error(error);
      });

  }, []);
  const onChangeField = useCallback(
    name => text => {
      setValue(name, text);
    },
    []
  );

  useEffect(() => {
    register('name');
    register('email');
    register('password');
  }, [register]);

  return (
    <View style={{marginLeft:40, marginTop:50}}>
        <TextInput
        autoCompleteType="name"
        keyboardType="email-address"
        textContentType="emailAddress"
        placeholder="name"
        onChangeText={onChangeField('name')}
      />
      <TextInput
        autoCompleteType="email"
        keyboardType="email-address"
        textContentType="emailAddress"
        placeholder="Email"
        onChangeText={onChangeField('email')}
      />
      <TextInput
        secureTextEntry
        autoCompleteType="password"
        placeholder="Password"
        onChangeText={onChangeField('password')}
      />
      <Button title="Submit" onPress={handleSubmit(onSubmit)} />
    </View>
  );

};

